mapper.update:
UPDATE spring_board
SET readcnt = readcnt + 1
WHERE b_num = #{boardNumber}

mapper.select:
SELECT NVL((
			SELECT 1 FROM spring_board
			WHERE b_num = #{boardNumber}AND b_pwd = #{boardPasswd}
			), 0) as state
FROM dual

mapper.update:
UPDATE spring_board
SET b_title = #{boardTitle},
	b_content = #{boardContent},
	b_date = sysdate

		, b_pwd = #{boardPasswd}

WHERE b_num = #{boardNumber}
